$sidebar-width: 280px;

// Mixins
@mixin flex-center {
  display: flex;
  align-items: center;
  justify-content: center;
}

@mixin button-base {
  padding: 8px 16px;
  border-radius: 4px;
  border: none;
  cursor: pointer;
  font-weight: 500;
  transition: all 0.2s ease;
}

.sidebar {
    position: fixed;
    left: 0;
    top: 0;
    width: $sidebar-width;
    height: 100vh;
    background: #fff;
    border-right: 1px solid rgba(9, 114, 251, 0.1);
    padding: 24px 0;

    .logo {
        padding: 0 24px;
        margin-bottom: 32px;

        h1 {
        font-size: 24px;
        font-weight: 600;
        margin: 0;
        }

        p {
        color: $secondary-color;
        margin: 4px 0 0;
        }
    }

    .division-info {
        padding: 0 24px;
        margin-bottom: 1rem;
        h3 {
            font-size: 18px;
