            }
        }

        &:hover {
            background-color: rgba($primary-color, 0.05);
        }

        &.router-link-active {
            background-color: rgba($primary-color, 0.1);
            color: $primary-color;
            svg{
                path{
                    fill: $secondary-color;
                }
            }
        }

        .icon {
            width: 24px;
            height: 24px;
            margin-right: 12px;
        }
        }
    }

    .logout-btn {
        width: calc(100% - 48px);
        margin: 24px 24px 0 24px;
